import { combineReducers,createStore } from "redux";
import counterReducer, { ICounterAction } from "./store/counterReducer";
import { ICounter } from "./store/counterReducer";

interface Props{
    counterReducer: ICounterAction
}

const reducer = combineReducers<Props>({
    counterReducer: counterReducer
})

const store = createStore(reducer);

export default store;
