import * as actionTypes from "../actionTypes";
import { IUser, IUserAction } from "../types/userTypes";

const initialState: IUser | {} = {
    id: 0,
    firstName: "",
    lastName: ""
};

const userReducer =
    (
    state = initialState,
    action: IUserAction
    ):IUserState => {
    
  switch (action.type) { 
      
    case actionTypes.SET_USER:
        const { user } = action;
        return { ...state, user};
      
    default:
      return state;
  }
};

export default userReducer;
